    // produk
    function add() {
        save_method = "add";
        $('#form').trigger('reset');
        $('#formTitle').html('Tambah Data');
        $('input[name=_method]').val('POST');
        $('#txtAction').html('');
        // $('#preview').attr({
        //     'src': '-',
        //     'alt': ''
        // });
        // $('#result').attr({
        //     'src': '-',
        //     'alt': ''
        // });
        // $('#changeText').html('Browse Image');
        $('#reset').show();
        // $('#reset').focus();
    }

    add();
    $('#form').on('submit', function(e) {
        if ($(this)[0].checkValidity() === false) {
            event.preventDefault();
            event.stopPropagation();
        } else {
            $('#alert').html('');
            url = (save_method == 'add') ? "{{ route('profile.'.'store') }}" : "{{ route('profile.'.'update', ':id') }}"
                .replace(':id', $('#id').val());
            $.ajax({
                url: url,
                type: (save_method == 'add') ? 'POST' : 'POST',
                data: new FormData(($(this)[0])),
                contentType: false,
                processData: false,
                success: function(data) {
                    console.log(data);
                    $('#alert').html(
                        "<div role='alert' class='alert alert-success alert-dismissible'><button type='button' class='close' data-dismiss='alert' aria-label='Close'><span aria-hidden='true'>Ã—</span></button><strong>Success!</strong> " +data.message + "</div>");
                    table.api().ajax.reload();
                    add();
                },
                error: function(data) {
                    err = '';
                    respon = data.responseJSON;
                    if (respon.errors) {
                        $.each(respon.errors, function(index, value) {
                            err = err + "<li>" + value + "</li>";
                        });
                    }
                    $('#alert').html(
                        "<div role='alert' class='alert alert-danger alert-dismissible'><button type='button' class='close' data-dismiss='alert' aria-label='Close'><span aria-hidden='true'>Ã—</span></button><strong>Error!</strong> " +
                        respon.message + "<ol class='pl-3 m-0'>" + err + "</ol></div>");
                        add();
                }
            });
            return false;
        }
        $(this).addClass('was-validated');
    });
    // produk

    function remove(id) {
        $.confirm({
            title: '',
            content: 'Apakah Anda yakin akan menghapus data ini ?',
            icon: 'icon icon-question amber-text',
            theme: 'modern',
            closeIcon: true,
            animation: 'scale',
            type: 'red',
            buttons: {
                ok: {
                    text: "ok!",
                    btnClass: 'btn-primary',
                    keys: ['enter'],
                    action: function() {
                        $.post("{{ route('profile.'.'destroy', ':id') }}".replace(':id', id), {
                            '_method': 'DELETE'
                        }, function(data) {
                            table.api().ajax.reload();
                            if (id == $('#id').val()) add();
                        }, "JSON").fail(function() {
                            reload();
                        });
                    }
                },
                cancel: function() {}
            }
        });
    }

    function edit(id) {
        save_method = 'edit';
        var id = id;
        $('#alert').html('');
        $('#form').trigger('reset');
        $('#formTitle').html(
            "Edit Data <a href='#' onclick='add()' class='btn btn-outline-danger btn-xs pull-right ml-2'>Batal</a>");
        $('#txtAction').html(" Perubahan");
        $('#reset').hide();
        // $('#result').attr({
        //     'src': '-',
        //     'alt': ''
        // });
        $('input[name=_method]').val('PATCH');
        $.get("{{ route('profile.'.'edit', ':id') }}".replace(':id', id), function(data) {
            $('#id').val(data.id);
            $('#nama').val(data.nama);
            $('#ram').val(data.ram);
            $('#memory_internal').val(data.memory_internal);
            $('#resolusi_layar').val(data.resolusi_layar);
            $('#baterai').val(data.baterai);
            $('#berat').val(data.berat);
            $('#harga').val(data.harga);
                }, "JSON").fail(function() {
            reload();
            });
    }

